import React, { Component } from 'react';
import './Carousel.css'; // Create a CSS file for styling if needed






class Carousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      images: ['image1.jpg', 'image2.jpg', 'image3.jpg'], // Add your image file names
      currentIndex: 0,
    };
  }

  componentDidMount() {
    // Start the auto-rotate interval
    this.interval = setInterval(this.rotateImages, 3000); // Adjust the interval as needed
  }

  componentWillUnmount() {
    // Clear the interval when the component unmounts
    clearInterval(this.interval);
  }

  rotateImages = () => {
    this.setState((prevState) => ({
      currentIndex:
        (prevState.currentIndex + 1) % this.state.images.length,
    }));
  };

  render() {
    const { images, currentIndex } = this.state;
    return (
      <div className="carousel-container">
        <div className="carousel-image">
          <img src={images[currentIndex]} alt="Carousel Image" />
        </div>
      </div>
    );
  }
}

export default Carousel;
