import React from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Stack from '@mui/material/Stack';
import "./styles.css";

const Signup = () => {
  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here
  };

  const textFieldStyle = {
    marginBottom: '20px', // Adjust the margin as needed to control the height
    width: '100%',
  };

  // Apply a custom style for larger text
  const largerText = {
    fontSize: '18px', // Adjust the font size as needed
  };

  return (
    <Box
      display="flex"
      flexDirection="column"
      alignItems="center"
      justifyContent="center"
      height="80vh"
      mx="auto"
      p={3}
      maxWidth="400px"
    >
      <form onSubmit={handleSubmit}>
        <Stack spacing={2} width="100%">
          <TextField
            label="Email"
            type="email"
            fullWidth
            style={{ ...textFieldStyle, ...largerText }}
          />
          <TextField
            label="Password"
            type="password"
            fullWidth
            style={{ ...textFieldStyle, ...largerText }}
          />
          <TextField
            label="Confirm Password"
            type="password"
            fullWidth
            style={{ ...textFieldStyle, ...largerText }}
          />
          <Button
            type="submit"
            variant="contained"
            color="primary"
            fullWidth
          >
            Sign Up
          </Button>
        </Stack>
      </form>
    </Box>
  );
};

export default Signup;
