import React from 'react';
import Feature from "./Feature.js";
import { Component } from 'react';
import './Slider.css'; // Create a CSS file for styling if needed

import pic01 from '../images/room1.jpeg'
import pic02 from '../images/room2.webp'
import pic03 from '../images/room3.webp'

class Sliders extends Component {
    constructor(props) {
        super(props);
        this.state = {
        features: [
            {
              id: 1,
              pic: pic01,
              address: "1234 Goon Street, Los Angeles, CA 90001, USA ",
              price: "$1,000/mo",
              description: "Charming bungalow with vintage appeal, nestled in a quiet neighborhood. Cozy and comfortable living awaits."
            },

            {
              id: 2,
              pic: pic02,
              address: "5678 Oak Avenue, Los Angeles, CA 90002, USA",
              price: "$1,250/mo",
              description: "Modern urban oasis with stunning city views. Stylish design, abundant natural light, and great amenities."
              },
            {
              id: 3,
              pic: pic03,
              address: "9876 Maple Lane, Los Angeles, CA 90003, USA ",
              price: "$1,200/mo",
              description: "Spacious family home with a backyard paradise. Ideal for entertaining, close to schools and parks."
            },

        ], // Add your features or data here
        currentOffset: 0,
        slideInterval: 1000, // Set your desired slide interval (in milliseconds)
        };
    }

    componentDidMount() {
      // Start the auto-rotate interval
      this.interval = setInterval(this.rotateComponents, 3000); // Adjust the interval as needed
    }

    componentWillUnmount() {
      // Clear the interval when the component unmounts
      clearInterval(this.interval);
    }

    rotateComponents = () => {
      this.setState((prevState) => {
        // Set the next component to be visible, and hide the previous one
        const newIndex = (prevState.currentIndex + 1) % this.state.data.length;
        const newVisibility = prevState.isVisible.map((_, index) =>
          index === newIndex
        );

        return {
          currentIndex: newIndex,
          isVisible: newVisibility,
        };
      });  
    };




      render() {
        const { features, isVisible, currentIndex } = this.state;

        return (
        //   <div className="carousel-container">
        //   {data.map((item, index) => (
        //     <CarouselComponent
        //       key={item.id}
        //       content={item.content}
        //       isVisible={isVisible[index]}
        //       currentIndex={currentIndex === index}
        //     />
        //   ))}
        // </div>

          <div className="slider-container">
            <div className="slider-wrapper">
              {features.map((item, index) => (
                 <Feature
                  key={item.id}
                  content={item}
                  isVisible={isVisible[index]}
                  currentIndex={currentIndex === index}
                 />
              )) }

            </div>
          </div>
        );
      }
}

export default Sliders;
